                        decoded_modules['syscollector'],                                        # 17
                        decoded_modules['vulnerability'],                                       # 18
                        decoded['agent'],                                                       # 19
                        decoded['dbsync'],                                                      # 20
                        decoded['monitor'],                                                     # 21
                        decoded['remote'],                                                      # 22

                        dropped_modules['azure'],                                               # 23
                        dropped_modules['ciscat'],                                              # 24
